import React, { useState } from "react";
import { fetchNews, listNews } from "../api";

export default function FetchNews() {
  const [status, setStatus] = useState(null);
  const [items, setItems] = useState([]);
  const [search, setSearch] = useState("");

  const onFetch = async () => {
    setStatus("fetching...");
    try {
      const r = await fetchNews(50);
      setStatus(`Fetched ${r.data.result.fetched}, added ${r.data.result.added}`);
      loadNews();
    } catch (err) {
      setStatus("error: " + (err?.message || String(err)));
    }
  };

  const loadNews = async () => {
    try {
      const r = await listNews(1, 50);
      setItems(r.data.items || []);
    } catch (e) {
      console.error(e);
    }
  };

  const filteredItems = items.filter(
    (it) =>
      it.title.toLowerCase().includes(search.toLowerCase()) ||
      (it.summary_text || "").toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="panel">
      <h2>Fetch latest headlines</h2>
      <div>
        <button onClick={onFetch}>Fetch from Google News RSS</button>
        <button onClick={loadNews} style={{ marginLeft: 8 }}>
          Load Stored News
        </button>
      </div>
      <div style={{ marginTop: 10 }}>
        <strong>Status:</strong> {status}
      </div>

      {/* ðŸ”Ž Search */}
      <div style={{ marginTop: 10 }}>
        <input
          type="text"
          placeholder="Search news..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ padding: "6px", width: "60%" }}
        />
      </div>

      <div className="news-list">
        {filteredItems.map((it) => (
          <div key={it.id} className="news-item">
            <a href={it.link} target="_blank" rel="noreferrer">
              {it.title}
            </a>
            <div className="meta">
              {it.published} â€¢ summarized: {String(it.summarized)}
            </div>
            {it.summary_text && <blockquote>{it.summary_text}</blockquote>}
          </div>
        ))}
      </div>
    </div>
  );
}
