import React, { useEffect, useState } from "react";
import { topics } from "../api";
import { Pie, Bar } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement } from 'chart.js';
ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

export default function Analytics(){
  const [data, setData] = useState(null);
  const [clusters, setClusters] = useState(5);

  const load = async () => {
    try {
      const r = await topics(clusters);
      setData(r.data);
    } catch(e){
      console.error(e);
      setData(null);
    }
  };

  useEffect(()=>{ load(); }, [clusters]);

  if(!data) return <div className="panel"><h2>Analytics</h2><p>Loading...</p></div>;

  const pieLabels = data.topics.map(t => `Topic ${t.cluster}`);
  const pieData = data.topics.map(t => t.count);
  const barLabels = data.topics.map(t => `T${t.cluster}: ${t.top_terms.slice(0,3).join(", ")}`);

  return (
    <div className="panel">
      <h2>Analytics</h2>
      <div>
        <label>Clusters: </label>
        <input type="number" value={clusters} min={1} max={12} onChange={(e)=>setClusters(Number(e.target.value))}/>
        <button onClick={load} style={{marginLeft:8}}>Refresh</button>
      </div>
      <div className="charts">
        <div className="chart">
          <h3>Topic distribution (pie)</h3>
          <Pie data={{ labels: pieLabels, datasets: [{ data: pieData, label: "count" }] }} />
        </div>
        <div className="chart">
          <h3>Topics (bar)</h3>
          <Bar data={{
            labels: barLabels,
            datasets: [{ label: "Items", data: pieData }]
          }} />
        </div>
      </div>

      <div style={{marginTop:16}}>
        <h4>Topic details</h4>
        {data.topics.map(t => (
          <div key={t.cluster} style={{border:"1px solid #ddd", padding:8, marginBottom:6}}>
            <strong>Topic {t.cluster} â€” {t.count} items</strong>
            <div>Top terms: {t.top_terms.join(", ")}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
